    function __construct(
        $accessToken,
        $accessTokenSecret,
        $accessTokenUrl,
        $authorizeUrl,
        $consumerKey,
        $consumerSecret,
        $requestTokenUrl
    ){
        $this->accessToken = $accessToken;
        $this->accessTokenSecret = $accessTokenSecret;
        $this->accessTokenUrl = $accessTokenUrl;
        $this->authorizeUrl = $authorizeUrl;
        $this->consumerKey = $consumerKey;
        $this->consumerSecret = $consumerSecret;
        $this->requestTokenUrl = $requestTokenUrl;
    }


    private function getApiAccessToken()
    {
        // Get Token
        $ch = curl_init();
        curl_setopt($ch,CURLOPT_URL, 'https://api.twitter.com/oauth2/token');
        curl_setopt($ch,CURLOPT_POST, true);
        $data = array();
        $data['grant_type'] = "client_credentials";
        curl_setopt($ch,CURLOPT_POSTFIELDS, $data);
        curl_setopt($ch,CURLOPT_USERPWD, $this->consumerKey . ':' . $this->consumerSecret);
        curl_setopt($ch,CURLOPT_RETURNTRANSFER, true);
        $result = curl_exec($ch);
        curl_close($ch);
        $bearer_token = json_decode($result);

        return $bearer_token->{'access_token'};
    }


    public function twitterTimeline($twitterUsername, $tweet_count=10)
    {

        if($this->localTest){
            echo $this->testTweets; exit;
        }
        //get api token
        $bearer = $this->getApiAccessToken();

        // Get Tweets
        $ch = curl_init();
        curl_setopt($ch,CURLOPT_URL, 'https://api.twitter.com/1.1/statuses/user_timeline.json?count='.$tweet_count.'&screen_name='.$twitterUsername);
        curl_setopt($ch,CURLOPT_HTTPHEADER,array('Authorization: Bearer ' . $bearer));
        curl_setopt($ch,CURLOPT_RETURNTRANSFER, true);
        $response = curl_exec($ch);
        curl_close($ch);


        return $response;

    }


    public function getAvailableWoeIds()
