    {

        if($this->localTest){
            echo $this->testTrends; exit;
        }
        $bearer = $this->getApiAccessToken();

        $ch = curl_init();
        curl_setopt($ch,CURLOPT_URL, 'https://api.twitter.com/1.1/trends/place.json?id='.$woeId);
        curl_setopt($ch,CURLOPT_HTTPHEADER,array('Authorization: Bearer ' . $bearer));
        curl_setopt($ch,CURLOPT_RETURNTRANSFER, true);
        $response = curl_exec($ch);
        curl_close($ch);

        return $response;

    }
